import{_ as e,V as i,W as a,Z as t}from"./framework-e5d7a6b2.js";const c={},l=t('<h1 id="事务特性-acid" tabindex="-1"><a class="header-anchor" href="#事务特性-acid" aria-hidden="true">#</a> 事务特性：ACID</h1><h2 id="四大特性" tabindex="-1"><a class="header-anchor" href="#四大特性" aria-hidden="true">#</a> 四大特性</h2><ul><li><p>原子性（Ato&#39;micity）</p><p>事务是不可分割的最小操作单元，要么全部成功，要么全部失败。</p></li><li><p>一致性（Con&#39;sistency）</p><p>事务完成时，必须使所有的数据都保持一致状态。</p></li><li><p>隔离性（Isolation）</p><p>数据库系统提供的隔离机制（隔离级别），保证事务在不受外部并发操作影响的独立环境下运行。</p></li><li><p>持久性（Dura&#39;bility）</p><p>事务一旦提交或回滚，他对数据库中数据的改变就是永久的。</p></li></ul>',3),r=[l];function n(o,s){return i(),a("div",null,r)}const d=e(c,[["render",n],["__file","1. ACID.html.vue"]]);export{d as default};
