import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{o as l,c as a,d as e}from"./app-9107e99b.js";const r={},p=e('<h1 id="概论" tabindex="-1"><a class="header-anchor" href="#概论" aria-hidden="true">#</a> 概论</h1><ul><li>基本概念</li><li>发展简史</li><li></li></ul><p>智能的特征</p><h2 id="基本概念" tabindex="-1"><a class="header-anchor" href="#基本概念" aria-hidden="true">#</a> 基本概念</h2><h3 id="智能" tabindex="-1"><a class="header-anchor" href="#智能" aria-hidden="true">#</a> 智能</h3><p>智能是知识与智力的总和</p><h3 id="智能的特征" tabindex="-1"><a class="header-anchor" href="#智能的特征" aria-hidden="true">#</a> 智能的特征</h3><ul><li><p>感知能力</p></li><li><p>记忆与思维能力</p><ul><li><p>逻辑思维（抽象思维）</p></li><li><p>形象思维（直感思维）</p></li><li><p>顿悟思维（灵感思维）</p></li></ul></li><li><p>学习能力</p></li><li><p>行为能力（表达能力）</p></li></ul><h3 id="人工智能" tabindex="-1"><a class="header-anchor" href="#人工智能" aria-hidden="true">#</a> 人工智能</h3><p>人工智能：用人工的方法在机器（计算机）上实现的智能；或者说是人们使机器具有类似于人的智能。</p><p>人工智能学科</p><p>图灵测试</p><p>中国屋思考实验：证明即使通过图灵测试也不能说明计算机能思维。</p><h2 id="发展简史" tabindex="-1"><a class="header-anchor" href="#发展简史" aria-hidden="true">#</a> 发展简史</h2><p>美国爱荷华州立大学的阿塔纳索夫教授和他的研究生贝瑞在1937年至1941年间开发的世界上第一台电子计算机“阿塔纳索夫－贝瑞计算机</p><h3 id="形成-1956-1969" tabindex="-1"><a class="header-anchor" href="#形成-1956-1969" aria-hidden="true">#</a> 形成 1956-1969</h3><p>麦卡锡提议正式采用“人工智能”这一术语，标志着人工智能学科正式诞生。</p><p>麦卡锡因而被称为<strong>人工智能之父</strong>。</p><p>IJCAI</p><h3 id="发展-1970" tabindex="-1"><a class="header-anchor" href="#发展-1970" aria-hidden="true">#</a> 发展 1970-</h3><ul><li><p>知识应用时期 1977。专家系统的研究在多领域取得重大突破。</p></li><li><p>集成发展时期 1986。计算智能 （ <strong>Computer Intelligence</strong>， <strong>CI</strong>）弥补了<strong>AI</strong>在数学理论和计算上的不足，丰富了<strong>AI</strong>理论框架，使人工智能进入了一个新的发展时期</p></li><li><p>大数据驱动发展期 2011-</p><p>物联网、大数据、云计算、人工智能相互促进</p><ul><li>专用人工智能。eg. AlphaGo</li><li>通用人工智能。AGI</li></ul></li></ul><h2 id="基本内容" tabindex="-1"><a class="header-anchor" href="#基本内容" aria-hidden="true">#</a> 基本内容</h2><p>知识表示：将人类知识形式化或者模型化。</p><p>知识表示方法：</p><ul><li>符号表示法。eg. 谓词逻辑、产生式</li><li>连接机制表示法。eg. 神经网络NN</li></ul><p>机器感知：机器视觉(machine vision)与机器听觉</p><p>机器思维：对通过感知得来的外部信息及机器内部的各种工作信息进行有目的的处理。</p><p>机器学习：</p><p>机器行为</p><h2 id="研究领域" tabindex="-1"><a class="header-anchor" href="#研究领域" aria-hidden="true">#</a> 研究领域</h2><p>博弈</p><ul><li>国际象棋比赛 <ul><li>深思</li><li>深蓝</li></ul></li><li>围棋比赛 <ul><li>DOC</li><li>AlphaGo</li></ul></li></ul><p>模式识别 pattern recognition</p><ul><li>文字识别：邮政编码、车牌识别、汉字识别。</li><li>人脸识别：反恐、商业。</li><li>物体识别：导弹、机器人。</li></ul><p>计算机视觉 computer vision 机器视觉 machine vision</p><p>智慧医疗</p><ul><li>医学影像识别</li><li>黑色素瘤识别</li></ul><p>自然语言理解</p><p>人工神经网络</p>',39),h=[p];function n(d,t){return l(),a("div",null,h)}const c=i(r,[["render",n],["__file","1. 概论.html.vue"]]);export{c as default};
