import{_ as c}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as i,c as l,e as a,a as e,b as d,d as t}from"./app-9107e99b.js";const n={},r=e("h1",{id:"vi",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#vi","aria-hidden":"true"},"#"),d(" VI")],-1),h=e("blockquote",null,[e("p",null,[d("教材 "),e("em",null,"vimtutor")])],-1),s=e("h2",{id:"状态",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#状态","aria-hidden":"true"},"#"),d(" 状态")],-1),u=e("p",null,"初始是 cmd 模式",-1),_=t('<ul><li><p>cmd</p><p>命令模式</p><p>浏览：<code>j</code>, <code>k</code> 上下调光标</p><p>检索：<code>/</code>，并输入搜索内容（支持正则表达式）、回车，就可以看到结果，按 <code>n</code> 下一个，<code>N</code> 上一个</p></li><li><p>insert</p><p>编辑模式</p><p>输入 <code>i</code>(或者 <code>a,o</code>)，进入编辑模式</p></li><li><p>Last line</p><p>末行模式</p><p>输入<code>:</code></p><ul><li><code>:w</code></li><li><code>:q</code></li><li><code>:wq</code></li><li><code>:q!</code></li></ul></li></ul><h3 id="cmd" tabindex="-1"><a class="header-anchor" href="#cmd" aria-hidden="true">#</a> CMD</h3><blockquote><p>数字通常代表重复做几次的意思</p><p><code>[n][CMD]</code>：重复执行 n 词 CMD 指令</p></blockquote><h4 id="浏览" tabindex="-1"><a class="header-anchor" href="#浏览" aria-hidden="true">#</a> 浏览</h4><p><code>j</code>, 上</p><p><code>k</code>, 光标移动下一行</p><p><code>&lt;enter&gt;</code>: 光标下移动一行，并且页面下移动一行</p><p><code>h</code>, 左</p><p><code>l</code>, <code>&lt;space&gt;</code>, 右</p><blockquote><p>多行移动</p><ul><li><code>[n][jkhi]</code> 向某方向移动 n 行/列</li><li><code>[n]&lt;space&gt;</code> 向右 n 列</li><li><code>[n]&lt;enter&gt;</code> 向下 n 行</li></ul></blockquote><p><code>&lt;ctl&gt;f</code>, 下一页</p><p><code>&lt;ctl&gt;b</code>, 上一页</p><p><code>&lt;ctl&gt;d</code>, 下半页</p><p><code>&lt;ctl&gt;u</code>, 上半页</p><p><code>G</code>: 移动到文件尾</p><p><code>[n]G</code>, <code>[n]gg</code>: 移动到第 n 行</p><p><code>gg</code>, <code>1G</code>: 到文件首</p><p><code>0</code>, <code>^</code>: 移动到行首</p><p><code>$</code>: 移动到行尾</p><p><code>w</code>: 下一个单词开头</p><p><code>b</code>: 上一个单词开头</p><p><code>d[0$wb]</code>: 删除行内 xxx</p><p><code>y[0$wb]</code>: 复制行内 xxx</p><p><code>d1G</code></p><p><code>dG</code></p><p><code>y1G</code></p><p><code>yG</code></p><p><code>V</code>: 选择当前行，可以加行切换，如 G，10G 等，选中这些行，可以复制剪切删除等</p><h3 id="检索" tabindex="-1"><a class="header-anchor" href="#检索" aria-hidden="true">#</a> 检索</h3><p><code>/</code>：搜索</p><h4 id="编辑" tabindex="-1"><a class="header-anchor" href="#编辑" aria-hidden="true">#</a> 编辑</h4><p><code>yy</code>: 复制所在一行</p><p><code>[n]yy</code>: 复制所在及向下共 n 行</p><p><code>dd</code>: 剪切</p><p><code>[n]dd</code>: 剪切所在及向下共 n 行</p><p><code>d0</code>, <code>d^</code>: 剪切当前行光标到行首所有字符</p><p><code>d$</code>: 剪切当前行光标到行尾所有字符</p><p><code>p</code>: 粘贴到光标的下一行</p><p><code>P</code>: 粘贴到光标的上一行</p><p><code>u</code>：undo，取消上一个指令</p><p><code>&lt;ctl&gt;r</code>: 重做上一步</p><p><code>.</code>: 重复执行上一个指令</p><p><code>x</code>：<strong>删除光标处的字符</strong></p><p><code>X</code>: 删除光标前一个字符</p><p><code>[n]x</code>: 删除光标及以后 n 个字符</p><p><code>J</code>：和下一行连接在一起（中间加空格）</p><h3 id="last-line" tabindex="-1"><a class="header-anchor" href="#last-line" aria-hidden="true">#</a> Last line</h3><p><code>q!</code>: 可以不存储就退出</p><p><code>wq</code>: 保存并推出</p><p><code>w</code>: 保存</p><p><code>w &lt;文件名&gt;</code>: 另存为</p><h4 id="替换" tabindex="-1"><a class="header-anchor" href="#替换" aria-hidden="true">#</a> 替换</h4><p>对于替换当前打开文件中的文本，vim 提供如下语法：</p><p><code>:{作用范围}s/{目标字符}/{替换的字符}/{替换标志}</code></p><p>作用范围：用于指定替换的范围</p><ul><li>不写就是当前行</li><li>.,+n 表示替换当前行至下 n 行</li><li>1,n 表示替换第一行至第 n 行，</li><li>1,$ 表示替换第一行到最后一行，也可以直接用%表示。</li></ul><p>替换标志（可以组合使用）：</p><ul><li>c: confirm，每次替换前都会询问</li><li>e：不显示 error</li><li>g: globe，不询问，整个替换</li><li>i: ignore，即不区分大小写</li></ul>',58);function m(g,x){const o=p("Mermaid");return i(),l("div",null,[r,h,s,u,a(o,{id:"mermaid-14",code:"eJxLL0osyFDwCeLidNRIzk3RVNDVtavJ1EnUya9RcNLIzCtOLSrR5OJ0Aokr1KQWJ9coOALVgpVZ1Sg4a+QkFpco5GTmpQJVOaOoAgCbCRku"}),_])}const k=c(n,[["render",m],["__file","1.2. vi.html.vue"]]);export{k as default};
