import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as r,c as e,d as i}from"./app-9107e99b.js";const h={},n=i('<h1 id="知识表示与知识图谱" tabindex="-1"><a class="header-anchor" href="#知识表示与知识图谱" aria-hidden="true">#</a> 知识表示与知识图谱</h1><h2 id="知识与知识表示的概念" tabindex="-1"><a class="header-anchor" href="#知识与知识表示的概念" aria-hidden="true">#</a> 知识与知识表示的概念</h2><h3 id="知识的概念" tabindex="-1"><a class="header-anchor" href="#知识的概念" aria-hidden="true">#</a> 知识的概念</h3><p>知识：把有关信息关联在一起所形成的信息结构。</p><blockquote><p>知识反映了客观世界中事物之间的关系，不同事物或者相同事物间的不同关系形成了不同的知识。</p></blockquote><h3 id="知识的特性" tabindex="-1"><a class="header-anchor" href="#知识的特性" aria-hidden="true">#</a> 知识的特性</h3><p>**1.**相对正确性</p><p><strong>2.</strong> 不确定性</p><ul><li>随机性引起的不确定性</li><li>模糊性引起的不确定性</li><li>经验引起的不确定性</li><li>不完全性引起的不确定性</li></ul><p><strong>3.</strong> 可表示性与可利用性</p><ul><li>知识的可表示性**😗* 知识可以用适当形式表示出来，如用语言、文字、图形、神经网络等。</li><li>知识的可利用性**😗* 知识可以被利用。</li></ul><h3 id="知识的表示" tabindex="-1"><a class="header-anchor" href="#知识的表示" aria-hidden="true">#</a> 知识的表示</h3><p>知识表示(<strong>knowledge representation</strong>)：将人类知识形式化或者模型化。</p><blockquote><p>知识表示是对知识的一种描述，或者说是一组约定，一种计算机可以接受的用于描述知识的数据结构。</p></blockquote><p>选择知识表示方法的原则:</p><ol><li>充分表示领域知识。</li><li>有利于对知识的利用。</li><li>便于对知识的组织、维护与管理。</li><li>便于理解与实现。</li></ol><h2 id="一阶谓词逻辑表示法" tabindex="-1"><a class="header-anchor" href="#一阶谓词逻辑表示法" aria-hidden="true">#</a> 一阶谓词逻辑表示法</h2><h3 id="命题" tabindex="-1"><a class="header-anchor" href="#命题" aria-hidden="true">#</a> 命题</h3><h3 id="谓词" tabindex="-1"><a class="header-anchor" href="#谓词" aria-hidden="true">#</a> 谓词</h3><h3 id="谓词公式" tabindex="-1"><a class="header-anchor" href="#谓词公式" aria-hidden="true">#</a> 谓词公式</h3><h3 id="谓词公式的性质" tabindex="-1"><a class="header-anchor" href="#谓词公式的性质" aria-hidden="true">#</a> 谓词公式的性质</h3><h3 id="一阶谓词逻辑知识表示方法" tabindex="-1"><a class="header-anchor" href="#一阶谓词逻辑知识表示方法" aria-hidden="true">#</a> 一阶谓词逻辑知识表示方法</h3><p>谓词公式表示知识的步骤:</p><p>(<strong>1</strong>)定义谓词及个体。</p><p>(<strong>2</strong>)变元赋值。</p><p>(<strong>3</strong>)用连接词连接各个谓词，形成谓词公式。</p><h3 id="一阶谓词逻辑表示法的特点" tabindex="-1"><a class="header-anchor" href="#一阶谓词逻辑表示法的特点" aria-hidden="true">#</a> 一阶谓词逻辑表示法的特点</h3><p>优点:</p><ol><li><p>自然性</p></li><li><p>精确性</p></li><li><p>严密性</p></li><li><p>容易实现</p></li></ol><p>局限性:</p><ol><li><p>不能表示不确定的知识</p></li><li><p>组合爆炸</p></li><li><p>效率低</p></li></ol><p>应用:</p><p>(<strong>1</strong>)自动问答系统(<strong>Green</strong>等人研制的<strong>QA3</strong>系统)</p><p>(<strong>2</strong>)机器人行动规划系统(<strong>Fikes</strong>等人研制的<strong>STRIPS</strong>系统)</p><p>(<strong>3</strong>)机器博弈系统(<strong>Filman</strong>等人研制的<strong>FOL</strong>系统)</p><p>(<strong>4</strong>)问题求解系统(<strong>Kowalski</strong>等设计的<strong>PS</strong>系统)</p><h2 id="产生式表示法" tabindex="-1"><a class="header-anchor" href="#产生式表示法" aria-hidden="true">#</a> 产生式表示法</h2><h3 id="产生式" tabindex="-1"><a class="header-anchor" href="#产生式" aria-hidden="true">#</a> 产生式</h3><h3 id="产生式系统" tabindex="-1"><a class="header-anchor" href="#产生式系统" aria-hidden="true">#</a> 产生式系统</h3><h2 id="框架表示法" tabindex="-1"><a class="header-anchor" href="#框架表示法" aria-hidden="true">#</a> 框架表示法</h2><h3 id="框架的一般结构" tabindex="-1"><a class="header-anchor" href="#框架的一般结构" aria-hidden="true">#</a> 框架的一般结构</h3><h2 id="知识图谱" tabindex="-1"><a class="header-anchor" href="#知识图谱" aria-hidden="true">#</a> 知识图谱</h2>',42),d=[n];function t(o,s){return r(),e("div",null,d)}const c=a(h,[["render",t],["__file","2. 知识表示与知识图谱.html.vue"]]);export{c as default};
