import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as s,c,a as e,b as a,e as r,d as i}from"./app-9107e99b.js";const h={},d=i('<h1 id="common" tabindex="-1"><a class="header-anchor" href="#common" aria-hidden="true">#</a> Common</h1><h2 id="academic-history" tabindex="-1"><a class="header-anchor" href="#academic-history" aria-hidden="true">#</a> Academic History</h2><h3 id="high-school" tabindex="-1"><a class="header-anchor" href="#high-school" aria-hidden="true">#</a> High School</h3><p>Zhengzhou Foreign Language School</p><p>Zhengzhou, Henan</p><p>China</p><p>July, 2024</p><h3 id="undergraduate" tabindex="-1"><a class="header-anchor" href="#undergraduate" aria-hidden="true">#</a> Undergraduate</h3><p>Northeastern University</p><p>Bachelor of Engineering</p><p>Sep 2020 - July 2024</p><h2 id="test" tabindex="-1"><a class="header-anchor" href="#test" aria-hidden="true">#</a> Test</h2><h3 id="toefl" tabindex="-1"><a class="header-anchor" href="#toefl" aria-hidden="true">#</a> TOEFL</h3><p>21/10/2023</p><p>‭4375810238999589‬</p><ul><li>R 30 30</li><li>L 28 28</li><li>S 22 23</li><li>W 24 24</li></ul><h3 id="gre" tabindex="-1"><a class="header-anchor" href="#gre" aria-hidden="true">#</a> GRE</h3><p>11/25/2023</p><p>2485091</p><ul><li>VR 148 32</li><li>QR 170 94</li><li>AW 3.0 15</li></ul><h2 id="biographical" tabindex="-1"><a class="header-anchor" href="#biographical" aria-hidden="true">#</a> Biographical</h2><p>Address</p><h3 id="pa" tabindex="-1"><a class="header-anchor" href="#pa" aria-hidden="true">#</a> PA</h3><p>No. 8 Suoling Rd, Jinshui District</p><p>Zhengzhou, Henan</p><p>450003</p><h3 id="ca" tabindex="-1"><a class="header-anchor" href="#ca" aria-hidden="true">#</a> CA</h3><p>Northeastern University</p><p>No. 195 Chuangxin Rd, Hunnan District</p><p>Shenyang, Liaoning</p><p>110100</p><p>110169</p><h2 id="courses" tabindex="-1"><a class="header-anchor" href="#courses" aria-hidden="true">#</a> Courses</h2><h3 id="math" tabindex="-1"><a class="header-anchor" href="#math" aria-hidden="true">#</a> Math</h3><p>Linear Algebra 93, Mathematical Culture A, Advanced Mathematics1(I) 95, Advanced Mathematics1(II) 93, Probability Theory and Mathematical Statistics 97, Numerical Analysis 92, Discrete Mathematics 95</p><h3 id="core-cs" tabindex="-1"><a class="header-anchor" href="#core-cs" aria-hidden="true">#</a> Core CS</h3><p>Object-Oriented Programming and Design 97, Operating Systems 95, Introduction to Database 83, Computer Networks 87, Software Construction and Testing 93, Algorithms Analysis and Design 94, Data Structures and Algorithms 92, Software Innovation Approaches and Case Studies 93, Software Project Management 94, Principles of Computer Organization 92</p><h3 id="ai" tabindex="-1"><a class="header-anchor" href="#ai" aria-hidden="true">#</a> AI</h3><p>Introduction to Artificial Intelligence 97, Introduction to Machine Learning 95, Intelligent Electronics and Information Technology 97, Introduction to Recommender Systems 91</p><h4 id="s" tabindex="-1"><a class="header-anchor" href="#s" aria-hidden="true">#</a> s</h4><p>Artificial Intelligence Technologies</p><p>A0801053510</p><p>2</p><hr><p>Situation and Policy(4)</p><h2 id="experiences" tabindex="-1"><a class="header-anchor" href="#experiences" aria-hidden="true">#</a> Experiences</h2><ul><li>Employment</li><li>Extracurricular Activities</li><li>Internship</li><li>Leadership</li><li>Research</li><li>Teaching Experience</li></ul><h3 id="internship" tabindex="-1"><a class="header-anchor" href="#internship" aria-hidden="true">#</a> Internship</h3>',48),l={id:"jd-com",tabindex:"-1"},p=e("a",{class:"header-anchor",href:"#jd-com","aria-hidden":"true"},"#",-1),u={href:"http://JD.com",target:"_blank",rel:"noopener noreferrer"},m=i('<p>Refactored the resource management service by implementing ConfigMap for automated and configurable resource-splitting solutions.<br> Utilized the Kubernetes Informer mechanism, along with observer pattern, asynchronous processing, row locking, and discard policies, to significantly improve the efficiency of platform resource recalculation and achieve a 20x faster service startup speed.<br> Optimized the CI/CD toolchain with cloud-native CI workflows using Argo Workflows and GitOps to trigger Argo CD.<br> Participated in the privatization deployment project for China Construction Bank (CCB) by building Helm charts to enable platform privatization deployment capabilities.<br> Refactored the activity page generation service for the AI-pilot Marketing platform, enhancing code structure and improving reusability by implementing the strategy pattern. Designed generation pipelines with AIGC services to generate activity content floors effectively.</p><h3 id="research" tabindex="-1"><a class="header-anchor" href="#research" aria-hidden="true">#</a> Research</h3><h4 id="bioinformatics-data-visualization" tabindex="-1"><a class="header-anchor" href="#bioinformatics-data-visualization" aria-hidden="true">#</a> Bioinformatics Data Visualization</h4><p>Contributed to the development of a bioinformatics data visualization platform, using the OViz framework to create diverse bioinformatics charts for the TIMEDB website. More details about the TIMEDB project can be found on the TIMEDB website.</p><p>Conducted data structuring, cleaning, and analysis, ensuring data quality and usability.</p><p>Developed user-friendly interfaces using Vue, enhancing data accessibility and user interaction.</p>',6),g={href:"https://academic.oup.com/nar/advance-",target:"_blank",rel:"noopener noreferrer"},f=i('<h4 id="cloud-based-game-experience" tabindex="-1"><a class="header-anchor" href="#cloud-based-game-experience" aria-hidden="true">#</a> Cloud-Based Game Experience</h4><p>Analyzed network performance, frame rates, resolution, and related data in cloud gaming. Explored factors impacting cloud gaming quality.</p><p>Utilized Python (NumPy, Pandas, and Matplotlib) for data analysis and Vue.js with ECharts for further visualization.</p><h3 id="teaching-experience" tabindex="-1"><a class="header-anchor" href="#teaching-experience" aria-hidden="true">#</a> Teaching Experience</h3><p>Secretary Academic Planning Center</p><p>Served as a lecturer for advanced mathematics during the winter academic support program and provided online study session assistance.</p><p>Orchestrated the planning of the spring semester academic support program, including drafting event proposals and promotional materials, and recruiting academic workshop instructors and volunteers.</p><p>Helped 400+ peers improve academic performance, leading to significant quiz score improvements.</p><h2 id="achievements" tabindex="-1"><a class="header-anchor" href="#achievements" aria-hidden="true">#</a> Achievements</h2><ul><li>Awards</li><li>Honor</li><li>Presentation</li><li>Publication</li><li>Scholarship</li></ul><p>National Scholarship<br> Merit Student Scholarship<br> Outstanding Student Pacesetter</p><p>ByteDance Youth Camp First Prize<br> Apple Mobile Application Innovation Contest Regional First Prize<br> Apple Mobile Application Innovation Second prize in the finals</p><h3 id="awards" tabindex="-1"><a class="header-anchor" href="#awards" aria-hidden="true">#</a> Awards</h3><p>ByteDance Youth Camp First Prize, 2022/09/30, ByteDance</p><p>Mobile Application Innovation Contest Regional First Prize, 2022/08/01, Apple Inc.</p><p>Mobile Application Innovation Contest Second prize in the finals, 2022/09/10, Apple Inc.</p><p>In the 2022 Apple Mobile Application Innovation Contest, I led my team to secure the national second prize with our project &quot;Trace Note,&quot; an AR social platform. This application allowed users to share experiences by placing virtual notes in real-world scenes and earn tokens. I implemented Aspect-Oriented Programming for user authentication and used Redis for distributed session management. I also developed a versatile cache tool class to address various cache-related issues, employed Redis Geo for efficient trace queries, and utilized RabbitMQ to implement the order-related features.</p><hr><p>At the 4th ByteDance Youth Camp, as a team leader, I steered our project &quot;Juejin&quot; to win the first prize. We built a personalized social recommendation system using the TrustSVD algorithm, effectively mitigating the cold start problem with users&#39; social connections. I developed the micro-service backend system. My role included implementing Spring Scheduler for daily recalculations of recommendation data and using Redisson for distributed locks, ensuring accurate and efficient recommendations.</p><h3 id="honor" tabindex="-1"><a class="header-anchor" href="#honor" aria-hidden="true">#</a> Honor</h3><p>Outstanding Student Pacesetter, 2021/10/09, Northeastern University</p><p>Outstanding Student, 2022/10/09, Northeastern University</p><h3 id="scholarship" tabindex="-1"><a class="header-anchor" href="#scholarship" aria-hidden="true">#</a> Scholarship</h3><p>National Scholarship, 2021/12/31, Northeastern University</p><p>Merit Student Scholarship, 2021/10/09, Northeastern University</p><p>Merit Student Scholarship, 2022/10/09, Northeastern University</p><p>Merit Student Scholarship, 2023/10/09, Northeastern University</p><h3 id="publication" tabindex="-1"><a class="header-anchor" href="#publication" aria-hidden="true">#</a> Publication</h3>',28),b=e("br",null,null,-1),v={href:"https://academic.oup.com/nar/advance-article/doi/10.1093/nar/gkac1006/6833242",target:"_blank",rel:"noopener noreferrer"},y=i('<p>Nucleic Acids Research</p><p>Xueying Wang, Lingxi Chen, Wei Liu, Yuanzheng Zhang, Dawei Liu, Chenxin Zhou, Shuai Shi, Jiajie Dong, Zhengtao Lai, Bingran Zhao, Wenjingyu Zhang, Haoyue Cheng, Shuaicheng Li</p><p>Citation: 5</p><h3 id="ad-info" tabindex="-1"><a class="header-anchor" href="#ad-info" aria-hidden="true">#</a> Ad Info</h3><p>University of California, Los Angeles</p><p>Carnegie Mellon University</p><p>Duke University</p><h3 id="apply-for-program" tabindex="-1"><a class="header-anchor" href="#apply-for-program" aria-hidden="true">#</a> Apply for Program</h3><p>University of California, Berkeley</p><p>University of California, Los Angeles</p><p>University of California, San Diego</p><p>Duke University</p><p>Carnegie Mellon University</p><p>Georgia Institute of Technology</p><h2 id="projects" tabindex="-1"><a class="header-anchor" href="#projects" aria-hidden="true">#</a> Projects</h2><p>For Yanxi AI platform, I refactored the resource management service by implementing ConfigMap for automated and configurable resource-splitting solutions.<br> Utilized the Kubernetes Informer mechanism, along with observer pattern, asynchronous processing, row locking, and discard policies, to significantly improve the efficiency of platform resource recalculation and achieve a 20x faster service startup speed.<br> Optimized the CI/CD toolchain with cloud-native CI workflows using Argo Workflows and GitOps to trigger Argo CD.<br> Participated in the privatization deployment project for China Construction Bank (CCB) by building Helm charts to enable platform privatization deployment capabilities.</p><hr><p>Refactored the activity page generation service for the AI-pilot Marketing platform, enhancing code structure and improving reusability by implementing the strategy pattern. Designed generation pipelines with AIGC services to generate activity content floors effectively.</p><hr><p>Developed the iTMS logistics system. Develop 9 micro services and 229 RESTful APIs with Spring Cloud, Redis, RabbitMQ, and Elastic Search. Using Vue, Vite, Arco Design, and Tailwind CSS to implement the reactive front-end website.</p><p>4367 4800 3984 1542</p><p>03/26</p><p>32</p>',23);function C(x,A){const t=o("ExternalLinkIcon");return s(),c("div",null,[d,e("h4",l,[p,a(),e("a",u,[a("JD.com"),r(t)])]),m,e("p",null,[a('Published contributions in "Nuclear Acid Research" (IF: 19.160/Q1). Details at '),e("a",g,[a("https://academic.oup.com/nar/advance-"),r(t)]),a(" article/doi/10.1093/nar/gkac1006/6833242.")]),f,e("p",null,[a("TIMEDB: tumor immune micro-environment cell composition database with automatic analysis and interactive visualization:"),b,e("a",v,[a("https://academic.oup.com/nar/advance-article/doi/10.1093/nar/gkac1006/6833242"),r(t)])]),y])}const w=n(h,[["render",C],["__file","biograph.html.vue"]]);export{w as default};
